package org.rpcf.tests.HelloWorld;

import org.rpcf.rpcbase.JRetVal;
import org.rpcf.rpcbase.JavaRpcContext;
import org.rpcf.rpcbase.RC;

import java.util.concurrent.TimeUnit;
public class maincli
{
    public static JavaRpcContext m_oCtx;
    public static String getDescPath( String strName )
    {
        String strDescPath =
            maincli.class.getProtectionDomain().getCodeSource().getLocation().getPath();
        String strDescPath2 = strDescPath + "/org/rpcf/tests/HelloWorld/" + strName;
        java.io.File oFile = new java.io.File( strDescPath2 );
        if( oFile.isFile() )
            return strDescPath2;
        strDescPath += "/" + strName;
        oFile = new java.io.File( strDescPath );
        if( oFile.isFile() )
            return strDescPath;
        return "";
    }
    public static void main( String[] args )
    {
        m_oCtx = JavaRpcContext.createProxy(); 
        if( m_oCtx == null )
            System.exit( RC.EFAULT );
        
        String strDescPath =
            getDescPath( "HelloWorlddesc.json" );
        if( strDescPath.isEmpty() )
            System.exit( RC.ENOENT );
        System.out.println("loading desc file " + strDescPath);
        // create the service object
        HelloWorldSvccli oSvcCli = new HelloWorldSvccli(
            m_oCtx.getIoMgr(), 
            strDescPath,
            "HelloWorldSvc" );

        // check if there are errors
        if( RC.ERROR( oSvcCli.getError() ) )
            System.exit( -oSvcCli.getError() );
        
        // start the proxy
        int ret = oSvcCli.start();
        if( RC.ERROR( ret ) )
            System.exit( -ret );
        
        do{
            // test remote server is not online
            while( oSvcCli.getState() == RC.stateRecovery )
            try{
                TimeUnit.SECONDS.sleep(1);
            }
            catch( InterruptedException e ){};
            
            if( oSvcCli.getState() != RC.stateConnected )
            { ret = RC.ERROR_STATE;break;}
            
            /*// request something from the server */
            JRetVal jret = oSvcCli.Echo(
                "Hello, World!" );
            if( jret.ERROR() )
            { ret = jret.getError();break; }

            String strResp = (String)jret.getAt(0);
            System.out.println(
                    "The server responsed with '" + strResp + "'" );

        }while( false );

        oSvcCli.stop();
        m_oCtx.stop();
        System.exit( -ret );
    }
}